const input = $input.all()[0]?.json;

let tasks = [];

if (Array.isArray(input)) {
  if (input[0]?.results) {
    tasks = input[0].results;
  } else if (input[0]?.content !== undefined) {
    tasks = input;
  }
} else if (input?.results) {
  tasks = input.results;
} else if (input?.data) {
  tasks = input.data;
}

let formattedText = '';

if (tasks.length > 0) {
  formattedText = tasks.map(task => {
    const name = task.content || 'Без названия';
    const description = task.description || '';
    const priority = task.priority || 4;
    let result = `- ${name}. Приоритет: ${priority}.`;
    if (description) {
      const isMultiline = description.includes('\n');
      if (isMultiline) {
        result += `\n  Описание:\n  ${description.replace(/\n/g, '\n  ')}`;
      } else {
        result += `\n  Описание: ${description}`;
      }
    }
    return result;
  }).join('\n');
} else {
  formattedText = 'Нет задач на сегодня';
}

return { json: { formattedTasks: formattedText } };

const input = $input.all()[0]?.json;

let tasks = [];

if (Array.isArray(input)) {
  if (input[0]?.results) {
    tasks = input[0].results;
  } else if (input[0]?.content !== undefined) {
    tasks = input;
  }
} else if (input?.results) {
  tasks = input.results;
} else if (input?.data) {
  tasks = input.data;
}

let formattedText = '';

if (tasks.length > 0) {
  // Сортируем по времени начала
  tasks.sort((a, b) => {
    const timeA = a.due?.date || '';
    const timeB = b.due?.date || '';
    return timeA.localeCompare(timeB);
  });

  formattedText = tasks.map(task => {
    const name = task.content || 'Без названия';
    const dueDate = task.due?.datetime || task.due?.date || '';
    const hasTime = dueDate.includes('T');
    const duration = task.duration;

    let startTime = '';
    let endTime = '';

    if (dueDate && hasTime) {
      const startDate = new Date(dueDate);
      startTime = startDate.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit', timeZone: 'Europe/Moscow' });

      if (duration && duration.amount) {
        const endDate = new Date(startDate);
        if (duration.unit === 'minute') {
          endDate.setMinutes(endDate.getMinutes() + duration.amount);
        } else if (duration.unit === 'hour') {
          endDate.setHours(endDate.getHours() + duration.amount);
        }
        endTime = endDate.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit', timeZone: 'Europe/Moscow' });
      }
    }

    if (startTime && endTime) {
      return `${startTime} - ${endTime} | ${name}`;
    } else if (startTime) {
      return `${startTime} | ${name}`;
    } else {
      return `-- : -- | ${name}`;
    }
  }).join('\n');
} else {
  formattedText = 'Нет событий в плане на сегодня';
}

return { json: { formattedTimeline: formattedText } };

const item = $input.all()[0];
let goalsText = 'Не удалось прочитать файл целей';

if (item.binary && item.binary.data) {
  const binaryData = await this.helpers.getBinaryDataBuffer(0, 'data');
  goalsText = binaryData.toString('utf-8');
} else if (item.json?.goalsContent) {
  goalsText = item.json.goalsContent;
} else if (item.json?.stdout) {
  goalsText = item.json.stdout;
}

return { json: { formattedGoals: goalsText } };

const input = $input.all();
let todoist = '';
let timeline = '';
let obsidian = '';

for (const item of input) {
  const data = item.json;
  if (data?.formattedTasks) todoist = data.formattedTasks;
  if (data?.formattedTimeline) timeline = data.formattedTimeline;
  if (data?.formattedGoals) obsidian = data.formattedGoals;
}

const day = $node['Config'].json.day;
const dayLabel = day === 'tomorrow' ? 'на завтра' : 'на сегодня';

const result = `# Задачи ${dayLabel}:\n\n${todoist}\n\n# Тайм-блокинг\n\n${timeline}\n\n# Цели\n\n${obsidian}`;

return { json: { combinedText: result } };
